import React, { useState } from 'react';
import { useAuth } from '../../../context/AuthContext';
import './DashboardLayout.css';

const DashboardLayout = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const { logout } = useAuth();

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className={`dashboard-layout ${sidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <span className="logo-text">Sunny</span>
          </div>
        </div>

        <div className="sidebar-content">
          <nav className="sidebar-nav">
            <a href="/dashboard" className="nav-item active">
              <span className="nav-text">Dashboard</span>
            </a>
            <a href="/transactions" className="nav-item">
              <span className="nav-text">Transactions</span>
            </a>
            <a href="/balances" className="nav-item">
              <span className="nav-text">Balances</span>
            </a>
          </nav>
        </div>
      </div>
      <div className="dashboard-main">
        <div className="topbar">
          <div className="topbar-left">
            <button className="menu-button" onClick={toggleSidebar}>
              <svg viewBox="0 0 24 24" width="24" height="24">
                <path fill="currentColor" d="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z"/>
              </svg>
            </button>
          </div>
          <div className="topbar-right">
            <div className="user-profile">
              <button onClick={logout} className="logout-button">
                Logout
              </button>
            </div>
          </div>
        </div>
        <div className="dashboard-content">
          {children}
        </div>
      </div>
    </div>
  );
};

export default DashboardLayout;
